# C++ Code:
```
class Solution {
public:
    vector<int> spiralOrder(vector<vector<int>>& mat) {
        int row=mat.size(), col=mat[0].size();

        vector<int> ans;

        int top=0,down=row-1;//pointer for our ROW
        int left=0,right=col-1;//pointers for our COLUMN

        int dir=0;//helps us in knowing in which direction to traverse

        //dirx: 0->left to right, 1->top to down
        //2->right to left  3->down to top
        while(top<=down && left<=right){

            if(dir == 0){
                for(int i=left; i<=right; i++) //L->R
                    ans.push_back(mat[top][i]);

                top += 1;     
            }

            else if(dir == 1){//T->D
                for(int i=top;i<=down;i++)
                    ans.push_back(mat[i][right]);

                right -= 1;
            }

            else if(dir == 2){//R->L
                for(int i=right; i >= left; i--)
                    ans.push_back(mat[down][i]);

                down -= 1;
            }

            else if(dir == 3){//D->T
                for(int i=down; i>= top ; i--)
                    ans.push_back(mat[i][left]);

                left += 1;
            }

            //change dirxn 
            dir = (dir + 1)%4;
        }

        return ans;
    }
};
