        int startingRow = 0;
        int startingCol = 0;

        int endingRow = row-1;
        int endingCol = col-1;

        while(count < total){

            // print starting row

            for(int index = startingCol; count < total && index <= endingCol; index++){
                ans.push_back(matrix[startingRow] [index]);
