class Solution {
public:
    int findMin(vector<int>& nums) {

        int aLo = 0;
        int aHi = nums.size() - 1;
        int aMid = 0;

        while (aLo < aHi) {
            if (nums[aLo] == nums[aHi]) {
                aHi--;
            } else {
                aMid = (aLo + aHi) >> 1;
                if (nums[aMid] > nums[aHi]) {
                    aLo = aMid + 1;
                } else {
                    aHi = aMid;
                }
            }
        }

        return nums[aLo];
    }
};
