    public int pivot(int[] arr){
        int piv =0;
        int start =0;
        int end = arr.length-1;
        while(start<=end){
            int mid = start + (end - start)/2;
            if(mid<end && arr[mid]>arr[mid+1]){
                piv = mid;
                break;
