class Solution {
     public int findMin(int[] nums) {

        if(nums[0] < nums[nums.length-1] || nums.length == 1)
            return nums[0];

        int idx = findMaxIdx(nums, 0, nums.length-1, nums.length);

        if(idx == -1) // Means all the elements in the array is similar.
            return nums[0];

        return nums[idx];
    }

    public int findMaxIdx(int nums[], int l, int r, int n)
    {
        if(l > r)
            return -1;

        int m = l+(r-l)/2;
        if(m+1 < n)
        {
            if(nums[m+1] < nums[m])
                return m+1;
        }

        int idx = findMaxIdx(nums, l, m-1, n);
        if(idx != -1)
            return idx;
        idx = findMaxIdx(nums, m+1, r, n);

        return idx;    
    }
}
