class Solution:
    def findMin(self, nums: List[int]) -> int:
        start = 0
        end = len(nums) - 1

        # Array has only one element
        if start == end:
            return nums[start]

        # If rotation leads to completely sorted array and all values aren't same.
        if nums[end] > nums[start]:
            return nums[start]

        # Now array is definitely rotated or has all values same.
        while start <= end:
            mid = start + ((end- start) // 2)

            # If the next value is lower than the previous value then next value will definitely be the minimum as the array is sorted.
            if mid > start and nums[mid - 1] > nums[mid]:
                return nums[mid]

            # If the next value is lower than the previous value then next value will definitely be the minimum as the array is sorted.
            if mid < end and nums[mid] > nums[mid + 1]:
                return nums[mid+1]

            # If values at start and end are equal, skip duplicates if possible
            if nums[start] == nums[end]:
                # Check if value at start+1 isn't the minimum.
                if nums[start] > nums[start+1]:
                    return nums[start+1]
                start += 1

                # Check if value at end isn't the minimum.
                if nums[end] < nums[end-1]:
                    return nums[end]
                end -= 1
            else:            
                # Since we have ensured that the array is rotated so if middle value is greater than the value at start of array then the minimum element will definitely be at right side of middle value ELSE on left side.
                if nums[mid] >= nums[start]:
                    start = mid + 1
                else:
                    end = mid

        # All values are same
        return nums[start]
