    return binarySearch(nums, 0 , nums.length - 1);
  }

  int binarySearch(int[] nums, int l, int h) {
    while(l < h) {
      int mid = (l + h) / 2;
      if(nums[mid] < nums[h]) h = mid;
      else if(nums[mid] > nums[h]) l = mid + 1;
      else h--;
    }
    return nums[l];
  }
}
