class Solution {
    public int findMin(int[] nums) {

        //Given: Ascending order + repeated values
        // As it is sorted, intutively we will go with binary search

        //First step, is to find the pivot element
        int pivot = pivot_func(nums);

        //As pivot element is the largest number in the array, 
        //we can easily say that number next to pivot will be the minimum element.

        return nums[pivot+1];
    }

    public int pivot_func(int[] nums)
    {
        int start = 0;
        int end = nums.length-1;

        while(start<=end)
        {
            int mid = start + (end-start)/2;

            //For case like : [...7 0 ...]
            //Mid: 7
            if(mid<end && nums[mid] > nums[mid+1])
            {
                return mid;
            }

            //For case like : [...7 0 ...]
            //Mid: 0            
            if(mid>start && nums[mid] < nums[mid-1])
            {
                return mid-1;
            }

            //For case like: [2 3 4 5 2 2 2]
            //Start: 2, Mid: 2, End: 2
            if(nums[mid] == nums[start] && nums[mid] == nums[end])
            {
                //We can now skip start and end as they are just duplicates
                //But first we will check if start/ end is pivot or not

                if(mid<end && nums[start] > nums[start+1])
                {
                    return start;
                }

                if(mid>start && nums[end] < nums[end-1])
                {
                    return end-1;
                }

                start++;
                end--;
            }

            //Now, for binary search we must divide the array into halves for searching

            else if(nums[start] < nums[mid] || nums[start] == nums[mid] && nums[mid] > nums[end])
            {
                start = mid+1;
            }
            else
            {
                end = mid-1;
            }
        }
        return -1;
    }

}
