var findMin = function(nums) {
  // this is variation of template 3
  // https://leetcode.com/explore/learn/card/binary-search/135/template-iii/936/
  // We start at negative 'lo', and setting 'hi' to the last element of the array
  // We assign lo to a new value when we know it is _not_ a possible solution
  let lo = -1, hi = nums.length - 1;

  // according to this while condition, at the end of the loop, 'lo' and 'hi' pointers
  // will be adjacent to each other. 'lo' will continue to point to the negative condition
  // and hi will point to the best candidate found at each iteration
  while (lo + 1 < hi) {
    let mid = lo + Math.floor((hi-lo)/2);

   // nums[mid] is lower than the best candidate we found, so we update the best
   // candidate stored in 'hi' to point to the new value.
   if(nums[mid] < nums[hi]) {
      hi = mid;

	  // if the number is higher than the best candidate ('hi' pointer), so this is not our
	  // answer. We further decrease the search space by bringing 'lo' closer to the estimate
	  // but we know that 'lo' is not the answer
    } else if (nums[mid] > nums[hi]) {
      lo = mid

	  // this is where it differs from the standard template of binary search
	  // and this is also the crux of this algorithm. Both the numbers are equal, the probable
	  // candidate (pointed by 'mid') and the best candidate ('hi'). To further decrease the
	  // search space, we decrease the value of 'hi' pointer by 1.
    } else {
      hi = hi - 1;
    }
  }
  // At the end of the loop, 'hi' points at our answer
  return nums[hi];
};
