                int notTake=0+next[prev+1];
                int take=-1e9;
                if(prev==-1 or (envelopes[prev][0]<envelopes[curr][0] and envelopes[prev][1]<envelopes[curr][1])){
                        take=1+next[curr+1];
                }
             currr[prev+1]=max(take,notTake); 
            }
            next=currr;
        }
        return currr[-1+1];
    }
};
