   int find_bound(vector<int> &dp,int target){

       int pos = dp.size();

       int low = 0;
       int high = dp.size()-1;

       while(low<=high){

           int mid = low + (high-low)/2;

           if(dp[mid]>=target){
               pos = mid;
               high = mid-1;
           }
           else{
               low = mid+1;
           }

       }

       return pos;

