class Solution {
    static int n;
    static int dp[];
    public int maxEnvelopes(int[][] a) {
        n=a.length;
        dp=new int[n+1];
        Arrays.fill(dp,-1);
        return dfs(a,n);        
    }

    private static int dfs(int a[][],int prev){
        if(dp[prev]!=-1) return dp[prev];

        int x=0,y=0;
        if(prev!=n) {
           x=a[prev][0];
           y=a[prev][1];
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if(a[i][0]>x && a[i][1]>y){
                ans=Math.max(ans,1+dfs(a,i));
            }
        }
        return dp[prev]=ans;
     }
}
