class Solution {

    int[] dp;

    public int lengthOfRussianDoll(int index, int[][] envelopes){

		// terminating condition aka base case
        if(index > envelopes.length-1)
            return 0;

        if(dp[index] > 0)
            return dp[index];

        int maxCount = 0;

		// main recursive module
        for(int i=index+1;i<envelopes.length;i++){
			// check if i envelope can be put inside index envelope
            if(envelopes[index][1] > envelopes[i][1] && envelopes[index][0] > envelopes[i][0])
                maxCount = Math.max(maxCount, lengthOfRussianDoll(i,envelopes)+1);
        }

        dp[index] = maxCount;

        return dp[index];
    }

    public int maxEnvelopes(int[][] envelopes) {
        // edge cases
        if(envelopes == null || envelopes.length == 0)
            return 0;

		// sort the array on envelopes with width descending order.
        Arrays.sort(envelopes, new Comparator<int[]>(){
            @Override
            public int compare(int[] a1, int[] a2){
                return a2[0] - a1[0];
            }
        });

		// dp[index] gives the maxium length of russian doll taking index as the outer most envelope.
        dp = new int[envelopes.length];

		// Now we consider creating the russian doll sequence with considering each envelope as the
		// outermost envelope and then moving forward. The important thing about sorting is than for any
		// index i ....only possible indexes will be greater than i. 
        int maxCount = 0;
        for(int i=0;i<envelopes.length;i++){
            maxCount = Math.max(maxCount, lengthOfRussianDoll(i,envelopes)+1);        
        }

        return maxCount;

    }
}
