class Solution {

    private int MAX_VALUE = 100002;

    public int maxEnvelopes(int[][] envelopes) {
        Arrays.sort(envelopes, (a, b) -> a[0] - b[0]);
        int[] prev = new int[]{MAX_VALUE, MAX_VALUE};
        return LIS(envelopes, envelopes.length - 1, prev);
    }

    private int LIS(int[][] envelopes, int index, int[] prev){
        if(index == 0){
            return (envelopes[0][0] < prev[0] && envelopes[0][1] < prev[1]) ? 1 : 0;
        }
        int exclude = LIS(envelopes, index - 1, prev);
        int include = 0;
        if(envelopes[index][0] < prev[0] && envelopes[index][1] < prev[1]){
            include = 1 + LIS(envelopes, index - 1, envelopes[index]);
        }

        return Math.max(exclude, include);
    }
}
