 class Solution {

    private int MAX_VALUE = 100002;

    public int maxEnvelopes(int[][] envelopes) {
        Arrays.sort(envelopes, (a, b) -> a[0] - b[0]);
        int[] prev = new int[]{MAX_VALUE, MAX_VALUE};
        int[][][] dp = new int[envelopes.length][MAX_VALUE + 1][MAX_VALUE + 1];

        for(int[][] grid : dp)
            for(int[] row : grid)
                Arrays.fill(row, -1);

        return LIS(envelopes, envelopes.length - 1, prev, dp);
    }

    private int LIS(int[][] envelopes, int index, int[] prev, int[][][] dp){
        if(index == 0){
            return (envelopes[0][0] < prev[0] && envelopes[0][1] < prev[1]) ? 1 : 0;
        }

        if(dp[index][prev[0]][prev[1]] != -1) return dp[index][prev[0]][prev[1]];
        int exclude = LIS(envelopes, index - 1, prev, dp);
        int include = 0;
        if(envelopes[index][0] < prev[0] && envelopes[index][1] < prev[1]){
            include = 1 + LIS(envelopes, index - 1, envelopes[index], dp);
        }

        return dp[index][prev[0]][prev[1]] = Math.max(exclude, include);
    }

}
