class Solution {
    public int maxEnvelopes(int[][] envelopes) {
        // sort the envelopes in ascending order based on width
        Arrays.sort(envelopes, (a, b) -> a[0] - b[0]); 

         // keep track of max envelopes could be Russian doll for each envelop.
        int[] dp = new int[envelopes.length];
        Arrays.fill(dp, 1);

        int max = 1;

        // i is the current envelop index
        // j is the previous envelop index before i
        for(int i = 0; i < envelopes.length; i++){
            for(int j = 0; j < i; j++){
                // compare the width and height of the current envelop with all the previous ones.
                if(envelopes[j][0] < envelopes[i][0] &&
                   envelopes[j][1] < envelopes[i][1])
                {
                    // update the max number of envelopes the current envelop could be Russian doll
                    dp[i] = Math.max(dp[i], dp[j] + 1);
                }
            }

            max = Math.max(dp[i], max);
        }

        return max;   
    }
}
