class Node {
    int width;
    int height;
    public List<Node> canHold = new ArrayList<>();

    @Override
    public String toString() {
        return "Node[" + this.width + "][" + this.height + "]";
    }
    Node(int width, int height) {
        this.width = width;
        this.height = height;
    }

    public void checkHold(Node n) {
        if(this.width > n.width && this.height > n.height) {
            canHold.add(n);
        }
    }
}
class Solution {
    public List<Node> path;
    public Map<Node, Integer> cache = new HashMap<>();
    public int getMaxDistanceHelper(Node n, int distance) {

        if(cache.get(n) == null) {
            int result;
            if(n.canHold.size() == 0) 
                result = distance;
            else {
                int max = 0;
                for(Node n2 : n.canHold) {
                    int temp = 1 + getMaxDistanceHelper(n2, distance);
                    if(temp > max)
                        max = temp;
                }
                result = max;            
            }   
            cache.put(n, result);
        }
        return cache.get(n);
    }
    public int maxEnvelopes(int[][] envelopes) {
        List<Node> nodes = new ArrayList<>();

        for(int i = 0; i < envelopes.length; i++) {
            nodes.add(new Node(envelopes[i][0], envelopes[i][1]));
        }
        for(int i = 0; i < nodes.size(); i++) {
            for(int j = 0; j < nodes.size(); j++) {
                if(i != j) 
                    nodes.get(i).checkHold(nodes.get(j));
            }
        }

        int max = 0;
        for(Node n: nodes) {

            int dist = getMaxDistanceHelper(n, 1);
            if(dist > max)
                max = dist;
        }
        return max;
    }
}
