public class Solution {

	// Find the longest subsequence of width
	private int LongestSubSequence(int[] sequence){
		List<int> seq = new List<int>();
		seq.Add(sequence[0]);
		for(int i=1;i<sequence.Length;i++){
			int num = sequence[i];
			if(sequence[i]>seq[seq.Count-1]){
				seq.Add(sequence[i]);
			}
			else{
				int nextLargestIndex = BinarySearch(seq,num);
				seq[nextLargestIndex] = num;
			 }      
		}

		return seq.Count;
	}

	//Use binary search to find the next largest element of the given number
	private int BinarySearch(List<int> seq, int num){
		int startIndex = 0;
		int endIndex = seq.Count-1;

		while(startIndex<endIndex){
			int midIndex = (startIndex)+(endIndex-startIndex)/2;

			if(seq[midIndex] == num)
				return midIndex;

			if(seq[midIndex]<num){
				startIndex = midIndex+1;
			}
			else{
				endIndex = midIndex;
			}
		}

		return startIndex;
	}
	public int MaxEnvelopes(int[][] envelopes) {
	   Array.Sort(envelopes, new CustomComparer());

		int[] widthSequence = new int[envelopes.Length];
		for(int i=0;i<envelopes.Length;i++){
			widthSequence[i] = envelopes[i][1];
		}

		return LongestSubSequence(widthSequence);
	}
}

