class Solution {
public:
    // compare returns when first element if pair is equal then it sorts in the decreasing order of the second element in the pair
    static bool compare(vector<int> a, vector<int> b) {
        if (a[0] == b[0])
            return a[1] > b[1];
        return a[0] < b[0];
    }

    int maxEnvelopes(vector<vector<int>>& env) {
        int n = env.size();
		// initialize dp with zero size -> ans will be size of the all elements pushed
        vector<int> dp;
        sort(env.begin(), env.end(), compare);
		// first element will always be in the dp as it is already sorted
        dp.push_back(env[0][1]);
        for(int i = 1; i < n; i++) {
            int cur = env[i][1];
			// returns an iterator pointing to the first element in the range [first, last) which has a value not less than val
            auto itr = lower_bound(dp.begin(), dp.end(), cur);
			// if all values are less than cur then its time to push the current inside dp
            if (itr == dp.end())
                dp.push_back(cur);
			// else if it is greater then replace the value already present at itr index with cur as cur is lesser
            else if(*itr > cur)
                dp[itr - dp.begin()] = cur;
        }
		// total size of dp will be the ans as you only inserted those element which are part of LIS
        return dp.size();
    }
};
```

