class Solution {
    public int maxEnvelopes(int[][] envelopes) {

        if(envelopes.length <= 1){
            return envelopes.length;
        }

        Arrays.sort(envelopes, (int[] r1, int [] r2) -> {
            if(Integer.compare(r1[0],r2[0]) == 0){
                return Integer.compare(r1[1],r2[1]);
            }
            return Integer.compare(r1[0],r2[0]);
        });

        cache = new Integer[envelopes.length + 1][envelopes.length + 1];

        return dp(envelopes, 0, -1);
    }

    Integer[][] cache;

    int dp(int[][] envelopes, int i, int prev){
        if(i >= envelopes.length){
            return 0;
        }

        if(cache[i][prev + 1] != null){
            return cache[i][prev + 1];
        }

        int take = 0;

        //can take current
        if(prev == -1 || (envelopes[i][0] > envelopes[prev][0] && envelopes[i][1] > envelopes[prev][1])){
            take = 1 + dp(envelopes, i + 1, i);
        }

        int notTake = dp(envelopes, i + 1, prev);

        int max = Math.max(take, notTake);

        cache[i][prev + 1] = max;

        return max;

    }
}
