class Solution {
public:
    using VI = vector<int>;
    using VVI = vector<VI>;
    int maxEnvelopes(VVI& A, int INF = 1e9 + 7) {
        int N = A.size();
        sort(A.begin(), A.end(), [](auto& a, auto& b) { return a[0] == b[0] ? b[1] < a[1] : a[0] < b[0]; });
        VI dp(N, INF);
        for (auto& pair: A) {
            auto [x, y] = tie(pair[0], pair[1]);
            auto i = distance(dp.begin(), lower_bound(dp.begin(), dp.end(), y));
            dp[i] = y;
        }
        return distance(dp.begin(), lower_bound(dp.begin(), dp.end(), INF));
    }
};
