class Solution {
    public int maxEnvelopes(int[][] envelopes) {
        Arrays.sort(envelopes, (a,b) -> { 
            int diff1 = a[0] - b[0];
            if(diff1!=0)
                return diff1;

            return b[1] - a[1];
        });

        return lis(envelopes);  
    }

    private int lis(int[][] nums){
        int n = nums.length;
        List<Integer> list = new ArrayList<>();

        for(int i = 0; i<n; i++){
            if(list.isEmpty() || list.get(list.size()-1) < nums[i][1])
                list.add(nums[i][1]);
            else{
                int idx = findCeilIndex(list, nums[i][1]);
                if(idx!=-1)
                list.set(idx, nums[i][1]);
            }
        }

        return list.size();
    }
    //returns index of smallest element >= x
    private int findCeilIndex(List<Integer> list, int x){
            int l = 0, h = list.size()-1;
                int idx = -1;
                while(l<=h){
                    int mid = l + (h-l)/2;
                    if(list.get(mid) == x){
                        idx = mid;
                        break;
                    }
                    if(list.get(mid) > x){
                        idx = mid;
                        h = mid-1;
                    } else
                        l = mid+1;
                }
        return idx;
    }
}
