class Solution {
    public int maxEnvelopes(int[][] envelopes) {
        int n=envelopes.length,len=1;
        if(n<=1) return n;
        Arrays.sort(envelopes,(a,b)->{
            if(a[0]==b[0]) return b[1]-a[1];
            else return a[0]-b[0];
        });
         int[][] sorted=new int[n][2];sorted[0]=envelopes[0];
        for(int[] arr : envelopes){
            int  index=insertion_position(sorted,arr,0,len-1);
            sorted[index]=arr;
            if(len==index ) len++;    
        }
       return len;
    }
    private int insertion_position(int[][] input,int[] arr,int st,int end){
       if(input[end][0]<arr[0] && input[end][1]<arr[1]) return end+1;
        while(st<=end){
            int mid=st+(end-st)/2;
            if(input[mid][1]>arr[1]) end=mid-1;
            else if(input[mid][1]<arr[1])  st=mid+1;
            else return mid;
        }
        return st;
    }
}
