class Solution {
  public int maxEnvelopes(int[][] envelopes) {
    int n = envelopes.length;
	// Sort the envelopes
    Arrays.sort(envelopes, (e1, e2) -> {
      if (e1[0] < e2[0]) return -1; // e1 first if smaller width
      if (e1[0] > e2[0]) return 1;  // e1 second if bigger width
      if (e1[1] < e2[1]) return -1; // e1 first if smaller height
      if (e1[1] > e2[1]) return 1; // e1 second if bigger height
      return 0; // preserve ordering if equal width and height
    });

	// idea is to have an auxillary array where dp[i] = number of envelopes that can fit
	// Because we sorted the envelopes from smallest to highest
	// we can traverse everything in the left of i
	// and look at all envelopes that might have been smaller
    int result = 0;
    int[] dp = new int[n];
    for (int i = 0; i < n; i++) {
      dp[i] = 1;
	  // look at all other smaller envelopes than enevelopes[i]
      for (int j = 0; j < i; j++) {
        if (bigger(envelopes[i], envelopes[j])) {
          dp[i] = Math.max(
            dp[j] + 1,
            dp[i]
          );
        }
      }

      result = Math.max(dp[i], result);
    }
    return result;
    // return recurse(envelopes, new LinkedList<>(), 0, envelopes.length);
  }

  // Time limit exceeded
  public int recurse(int[][] remaining, List<int[]> chosen, int pos, int n) {
    if (pos == n)
      return 0;

    int count = 0;
    int[] envelope = remaining[pos];

    int resultIfChosen = 0, resultIfNOTChosen = 0;

    int prev = chosen.size() - 1;
    if (chosen.isEmpty() || bigger(envelope, chosen.get(prev))) {
		// choose to include envelope in russian doll
		chosen.add(envelope);
		resultIfChosen = 1 + recurse(remaining, chosen, pos+1, n);
		chosen.remove(chosen.size() - 1); // backtrack
	}

	// choose to NOT include envelope in russian doll
    resultIfNOTChosen = recurse(remaining, chosen, pos+1, n);

    return Math.max(resultIfChosen, resultIfNOTChosen);
  }

  private boolean bigger(int[] e1, int[] e2) {
    return e1[0] > e2[0] && e1[1] > e2[1];
  }
}
