```
 class Solution {
    public int maxEnvelopes(int[][] envelops) {
        // so the envelop: width, height
        // sort the envelop with width in asending order, if there are envelop with the same width
        // arrange them with height in descending order, and then try to find the longest increasing subsequence 
        // in this , by height 

        // sanity check 
        if(envelops == null || envelops.length == 0 || envelops[0] == null || envelops[0].length != 2) return 0;

        // sort the envelops by the width in increasing order , if two width is the same, sort by height decreaisng order 
        Arrays.sort(envelops, (e1, e2) -> e1[0] == e2[0] ? e2[1] - e1[1] : e1[0] - e2[0]);
            // Collections.sort(personList, (p1, p2) -> p1.firstName.compareTo(p2.firstName));
        int[] array = new int[envelops.length];
        for(int i = 0; i < envelops.length; i++){
            array[i] = envelops[i][1];
        }
        return lis(array);
    }



    private int lis(int[] nums) {
        if(nums == null || nums.length == 0) return 0;

        List<Integer> res = new ArrayList<>();
        res.add(nums[0]);
        for(int i = 1; i < nums.length; i++){
            int cur = nums[i];
            if(cur == res.get(res.size() - 1)) continue;
            if(cur > res.get(res.size() - 1)){
                res.add(cur);
            }else{
                int index = bs(res, cur);
                // replacement 
                res.set(index, cur);
            }
        }
        return res.size();
    }
    private int bs(List<Integer> res, int newNum){
        // find the index of the element in the list, which is the first number that is bigger than the new element 
        int left = 0;
        int right = res.size() - 1;
        while(left < right){
            int mid = left + (right - left) / 2;
            if(res.get(mid) == newNum){
                return mid;
            }else if(res.get(mid) > newNum){
                right = mid;
            }else{
                // res.get(mid) < newNum
                left = mid + 1;
            }
        }
        return left;
    }

}
