class Solution {
public:
    int fourSumCount(vector<int>& nums1, vector<int>& nums2, vector<int>& nums3, vector<int>& nums4) {

       // idea: a+b+c+d = 0  =>  a+b = -(c+d) ;
       // store a+b in hash map for first two array combinations
       // later for last two array combinations check if sum exist in array

       unordered_map<int, int> hmap; 

       for( auto a: nums1){
           for(auto b: nums2){
               ++hmap[a+b]; // store and a+b and increment their frequencies also
           }
       }

      int count = 0;
      for(auto c: nums3){
          for(auto d: nums4){
              auto iter = hmap.find(0-c-d);
              if(iter != hmap.end() ){ 
                  count+= iter->second; 
                  // just incrementing count is not enough because duplicates tuple is also valid
              }
          }
      }


        return count;
    }
};
```

