        System.out.println(sumABFrequencyMap);

        for(int c : C){
            for(int d : D){
                int sumCD = c+d;
                if(sumABFrequencyMap.containsKey(-sumCD)){
                    sumCount += sumABFrequencyMap.get(-sumCD);
                }
            }
        }

        return sumCount;
    }
