class Solution {
    public int fourSumCount(int[] A, int[] B, int[] C, int[] D) {
        Map<Integer, Integer> counter = new HashMap<>();
        int n = A.length;
        for (int i = 0; i<n; i++){
            for (int j = 0; j <n;j++){
                Integer curr = counter.get(A[i]+B[j]);
                curr = curr == null ? 1 : curr + 1;
                counter.put(A[i]+B[j],curr);
            }
        }

        int res = 0;
        for (int i = 0; i<n; i++){
            for (int j = 0; j <n;j++){
                int findValue = -C[i] - D[j];
                Integer curr = counter.get(findValue);
                if (curr != null){
                    res += curr;
                }
            }
        }
        return res;
    }
}
