class Solution(object):
    def biSearch(self, nums, x, LEFT):
        l = 0
        r = len(nums)-1
        m = 0 
        while (l <= r) :
            m = (l+r)>>1
            if (LEFT):
                if (nums[m] >= x): r = m - 1
                else: l = m + 1
            else:
                if (nums[m] <= x): l = m + 1
                else: r = m - 1
        return l if LEFT else r

    def fourSumCount(self, A, B, C, D):
        s, m = list(), list()
        sum, ans, n = 0,0, len(A)

        for i in range(n):
            for j in range(n):
                s.append(A[i]+B[j])
                m.append(C[i]+D[j])
        s.sort()
        m.sort()
        for i in range(len(s)):
            if i != 0 and s[i]==s[i-1]:
                ans+=sum
            else:
                sum = self.biSearch(m, -s[i],False)-self.biSearch(m,-s[i],True) +1
                ans += sum
        return ans
