        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                int sum = A[i]+B[j];
                map.put(sum,map.getOrDefault(sum,0)+1);
            }
        }
        int result=0;
        HashMap<Integer,Integer> map2 = new HashMap<>();
        for(int i=0;i<n;i++){
            if(map2.containsKey(C[i])){
                result+=map2.get(C[i]);
                continue;
            }
            int increment=0;
            for(int j=0;j<n;j++){
                int sum = 0-C[i]-D[j];
                if(map.containsKey(sum)){
                    increment+=map.get(sum);
                }
            }
            result+=increment;
            map2.put(C[i],increment);
        }
        return result;
    }
}
