public int fourSumCount(int[] A, int[] B, int[] C, int[] D) {
    return kSumCount(new int[][]{A, B, C, D});
}

public int kSumCount(int[][] lists) {
    Map<Integer, Integer> m = new HashMap<>();
    addToHash(lists, m, 0, 0); // here, add the first half of arrays. if odd, then less than half.
    return countComplements(lists, m, lists.length / 2, 0); // here, subtract from the rest of the arrays.
}

// think about DFS case, for the first level, there are n nodes of array 0. each node will have n children from array 1 on the next level.
void addToHash(int[][] lists, Map<Integer, Integer> m, int i, int sum) { // note that variable i means array number (from 0).
    if (i == lists.length / 2) // at this level, sum is ready. just add to the number of cases having the same sum.
        m.put(sum, m.getOrDefault(sum, 0) + 1);
    else
		// this loop tries to add from current array (numbered i)
        for (int a : lists[i]) 
			// what this is trying to do is adding array i+1 to sum when element a from array i is added.
			// note that i & sum are local variable (parameters) so called method won't affect these values when returned.
            addToHash(lists, m, i + 1, sum + a); 
}
// the same idea here. we subtract, instead, until the level of empty level reaches.
int countComplements(int[][] lists, Map<Integer, Integer> m, int i, int complement) {
    if (i == lists.length)
        return m.getOrDefault(complement, 0);
    int cnt = 0;
    for (int a : lists[i])
        cnt += countComplements(lists, m, i + 1, complement - a);
    return cnt;
}
