        if(level == ((nums.length+1)/2)) {
            // we have reached the end of our dfs so add the sum
            sums.put(sum, sums.getOrDefault(sum, 0)+1);
            return;
        }

        // we do dfs over the array where the current level elements are neighbors an we visit each neighbor's children
        for(int i = 0; i < nums[level].length;i++) {
            getFirstHalfMap(nums, sums, level+1, sum+nums[level][i]);
        }
    }

    public void setTupleCount(int[][] nums, HashMap<Integer, Integer> sums, int level, int sum) {
        // we want to get the complement of sums of the second half of the arrays
        // so we define our base case as the stop condition of reaching the end of the second half
        if(level == nums.length) {
            int complement = 0-sum;
            // we have reached the end of our dfs, no more children so check if the complement exists
            if(sums.containsKey(complement)) {
                tupleCount += sums.get(complement);
            }
            return;
        }

        // dfs again
        for(int i = 0; i < nums[level].length;i++) {
            setTupleCount(nums, sums, level+1, sum+nums[level][i]);
        }
    }
}

