class Solution:

    def helper(self, lists):
        c = collections.Counter()
        for tup in itertools.product(*lists):
            c[sum(tup)] += 1
        return c

    def kSumII(self, lists):
        half = len(lists) // 2
        c1 = self.helper(lists[:half])
        c2 = self.helper(lists[half:])
        return sum(c1[val]*c2[-val] for val in c1)


    def fourSumCount(self, A: List[int], B: List[int], C: List[int], D: List[int]) -> int:
        return self.kSumII([A, B, C, D])
