	{
		for(int j = 0; j < B.Length; j++)
		{
			int sum = A[i] + B[j];        
			if(pairSums.ContainsKey(sum))
			{
				pairSums[sum]++;
			}
			else
			{
				pairSums.Add(sum, 1);
			}
		}
	}

	int count = 0;

	for(int i = 0; i < C.Length; i++)
	{
		for(int j = 0; j < D.Length; j++)
		{
			int sum = -1 * (C[i] + D[j]);        
			if(pairSums.ContainsKey(sum))
			{
				count += pairSums[sum];
			}
		}
	}

	return count;
}
