            mid=(low+high)/2;
            if(side.equals("right") && arr.get(mid)==val){
                index = mid;
                low=mid+1;
            }else if(side.equals("left")&&arr.get(mid)==val){
                index = mid;
                high=mid-1;
            }else if(arr.get(mid)>val){
                high=mid-1;
            }else 
                low=mid+1;
        }
        return index;
