class Solution:
    def leastBricks(self, wall: List[List[int]]) -> int:
        countToBrickIds = collections.defaultdict(list)

        for row in range(len(wall)):
            countToBrickIds[wall[row][0]].append((row,0))


        heap = []
        visited = set()
        for count in countToBrickIds:
            visited.add(count)
            heapq.heappush(heap,count)

        result = 0
        limit = sum(wall[0])

        while len(heap) > 0:
            minCount = heapq.heappop(heap)
            indices = countToBrickIds[minCount]
            if minCount != limit:
                result = max(result,len(indices))
            for row,col in indices:
                if col+1 < len(wall[row]):
                    newCount = minCount+wall[row][col+1]
                    if newCount not in visited:
                        heapq.heappush(heap,newCount)
                        visited.add(newCount)
                    countToBrickIds[newCount].append((row,col+1))
            countToBrickIds[minCount] = []

        return len(wall)-result

```

