class Solution {
    public int leastBricks(List<List<Integer>> wall) {
        Map<Integer, Integer> partitionCount = new HashMap<>();
        int rowLen=wall.size();
        int cols = 0;
        int max = 0;
        for(int brick: wall.get(0))
            cols += brick;
        for(List<Integer> row: wall){
            int partition = 0;
            for(int brick: row){
                partition += brick;
                if(partition < cols){
                    partitionCount.put(partition, partitionCount.getOrDefault(partition, 0)+1);
                    max = Math.max(max, partitionCount.get(partition));
                }
            }
        }
        return rowLen-max;
    }
}
