    public int leastBricks(List<List<Integer>> wall) {

		int maxCount = 0; //maximum bricks ending at a single boundary

        Map<Integer , Integer> map = new HashMap<>();//to store count of no. of bricks ending at same boundary

		for(List<Integer> row : wall){

            int boundary = 0; //boundary is cumulative 

            for(int brick = 0 ; brick < row.size() - 1 ; brick++){          //leaving last brick as we can't consider last edge

                boundary += row.get(brick);                                 //finding edge of each brick
                map.put(boundary , map.getOrDefault(boundary , 0) + 1);     //updating edge count in map
                maxCount = Math.max(maxCount , map.get(boundary));          //updating maxCount if possible

            }
        }
        return wall.size() - maxCount; //minimum number of bricks crossed
    }
}

