class Solution {
    public int leastBricks(List<List<Integer>> wall) {

        Map<Integer,Integer> mp = new HashMap<>();
        for(List<Integer> brick : wall){
            int pos = 0 ; //position of gap in every row
          for(int i = 0; i < brick.size() - 1; i++){
             int p = brick.get(i);
             pos = pos + p;
            //Mark the position for the gap and maintain the freq of a particular gap
             mp.put(pos,mp.getOrDefault(pos,0) + 1);
          }
        }
       int max = 0 ; //maximum number of gaps

       for(int key : mp.keySet()){
          max = Math.max(max,mp.get(key));
       }
      // answer will be the number of rows - the max number of gaps for a vertical line (As your vertical line will pass through maximum number of gaps thus reduces the number of bricks)
      return wall.size() - max;
    }
}
