class Solution:
    def leastBricks(self, A: List[List[int]]) -> int:
        N = len(A)
        m = {}
        for i in range(N):
            for j in range(0, len(A[i])):
                A[i][j] += A[i][j - 1] if 0 < j else 0              # 🔑 absolute offset
                m[A[i][j]] = 1 + m[A[i][j]] if A[i][j] in m else 1  # 🗺 absolute offset count
        last = max(m.keys()); del m[last]                           # 🚫 last absolute offset is invalid
        return N - max(m.values()) if len(m) else N                 # 🎯 N rows minus max same absolute offset count
