                A[i].add(input[i][j] + if (0 < A[i].size) A[i].last() else 0)  // 🔑 absolute offset
                m[A[i].last()] = 1 + m.getOrDefault(A[i].last(), 0)            // 🗺 absolute offset count
            }
        }
        var last = m.keys.max()!!; m.remove(last)                              // 🚫 last absolute offset is invalid
        var best = m.values.max()
        return N - (best ?: 0)                                                 // 🎯 N rows minus max same absolute offset count
    }
}
