class Solution {
public:
    using VI = vector<int>;
    using VVI = vector<VI>;
    using Map = unordered_map<int, int>;
    int leastBricks(VVI& A, Map m = {}, int last = -1, int best = -1) {
        int N = A.size();
        for (auto i{ 0 }; i < N; ++i)
            for (auto j{ 0 }; j < A[i].size(); ++j)
                A[i][j] += 0 < j ? A[i][j - 1] : 0,  // 🔑 absolute offset
                ++m[A[i][j]];                        // 🗺 absolute offset count
        for (auto [key, _]: m)
            last = max(last, key);
        m.erase(last);                               // 🚫 last absolute offset is invalid
        for (auto [_, cnt]: m)
            best = max(best, cnt);
        return !m.empty() ? N - best : N;            // 🎯 N rows minus max same absolute offset count
    }
};
