class Solution:
    def leastBricks(self, wall: List[List[int]]) -> int:
        # maintaining a dictionary with default values
        mp = defaultdict(int)
        for i in wall:
            len_ =len(i)
            # If number of bricks in a row is 1 then no edges
            if len_ <= 1:
                continue
            # start finding the edges and count the occurences simultaneously
            prefSum = i[0]
            mp[prefSum]+=1
            for j in range(1, len_):
                prefSum+=i[j]
                if j!=len_-1:
                    mp[prefSum]+=1
        wall_len = len(wall)
        # if no edges in any of the row, then return number of rows
        if len(mp) == 0: return wall_len
        # find number of bricks crossed
        return wall_len - max(mp.values())
