import heapq
class Solution:
    def leastBricks(self, wall: List[List[int]]) -> int:

        heap = []
        for row in wall:
            last_num = 0
            for brick in row:
                last_num = last_num + brick
                heapq.heappush(heap, last_num)

        cur_min_edge = heapq.heappop(heap)
        result = float('inf')
        same_edge_counter = 1
        while heap:
            current_edge = heapq.heappop(heap)
            if cur_min_edge != current_edge:
                result = min(len(wall)-same_edge_counter, result)
                cur_min_edge = current_edge
                same_edge_counter = 1
            else:
                same_edge_counter += 1
        if result == float('inf'):
            return same_edge_counter
        return result
