class Solution {
public:
    int leastBricks(vector<vector<int>>& wall) {
        // Cumulate all the bricks width
        for(auto& vec : wall){
            for(int i = 1 ; i < vec.size() ; ++i){
                vec[i] += vec[i-1];
            }
        }

        // Use a hashmap to keep track of how many times did we see that width
        unordered_map<int,int> mp;
        for(auto& vec : wall){
            for(int i = 0 ; i < vec.size() - 1 ; ++i){
                mp[vec[i]]++;
            }
        }

        // Find the maximum value
        int maxm = 0;
        for(auto& p : mp){
            if(p.second > maxm)
                maxm = p.second;
        }

		//Answer is wall.size() - maxm
        return wall.size() - maxm;
    }
};
