class Solution {
    // prefix sum of each row
    // count frequency with a map
    // keep track of highest frequency
    // cannot be 0 or sum -> exclude 0 and sum from max collision point
    // return : height - frequency

    public int leastBricks(List<List<Integer>> wall) {
        int maxFrequencyCount = Integer.MIN_VALUE;

        Map<Integer, Integer> count = new HashMap<>();
        for(int i = 0 ; i < wall.size(); i++) {
            int sum = 0;
            List<Integer> row = wall.get(i);
            for(int j = 0; j < row.size(); j++) {
                sum += row.get(j);
                count.put(sum, count.getOrDefault(sum, 0) + 1);
                if(j != row.size() - 1
                        && sum != 0
                        && count.get(sum) > maxFrequencyCount) {
                    maxFrequencyCount = count.get(sum);
                }
            }
        }
        if(maxFrequencyCount > 0) {
            return wall.size() - maxFrequencyCount;
        } 
        return wall.size();
    }
}
