class Solution {
public:
    int leastBricks(vector<vector<int>>& wall) {

        int rows = wall.size();       //define the wall height

        map<int, int> m;              //map m to store the number of common edges among the rows

        int max_edge = 0;             //variable to store the greatest number of common edges

        for(int i=0;i<rows;i++){                //for loop down the wall

            int curr = 0;                       //variable to accumulate the row widths

            for(int j=0;j<wall[i].size()-1;j++){    //nested for loop looking at each row

                curr += wall[i][j];                 //accumulating the width of each row

                m[curr]++;                          //populating the map with incremental edge counts per row

                max_edge = max(max_edge, m[curr]);  //finding the largest edge count site

            }

        }

        int ans = rows-max_edge;                    //define the answer as total rows minus max edge count location

        return ans;                                 //return the answer
    }
};

