class Solution {
public:
    int leastBricks(vector<vector<int>>& wall) {
        int height=wall.size();
        unordered_map<int,int>hash;
        int width=0;int maximum=0;
        for(int &n:wall[0])width+=n;
        for(vector<int>&row:wall){
            int sum=0;
            for(int &n:row){
                sum+=n;
                if(sum==width)break;
                if(hash.find(sum)==hash.end()){
                    hash[sum]=1;
                }
                else{
                    hash[sum]++;
                }
            }
        }
        for(auto it=hash.begin();it!=hash.end();it++){
            maximum=max(maximum,it->second);
        }
        return height-maximum;
    }
};
