class Solution {
    public int leastBricks(List<List<Integer>> wall) {

        List<Tuple> flat = new ArrayList<>();
        for(List<Integer> list : wall) {
            int begin = 0;
            for(int i : list) {
                int end = begin + i;
                flat.add(new Tuple(begin, end));
                begin = end;
            }
        }

        PriorityQueue<Tuple> pq = new PriorityQueue<>((o1, o2) -> (o1.end - o2.end));
        Collections.sort(flat);

        int result = Integer.MAX_VALUE;
        for(int i=0; i<flat.size(); i++) {
            Tuple tuple = flat.get(i);
            if(tuple.start == 0) {
                pq.add(tuple);
                continue;
            }

            while(!pq.isEmpty() && pq.peek().end <= tuple.start) {
                pq.poll();
            }
            result = Math.min(result, pq.size());
            pq.add(tuple);
        }

        return result == Integer.MAX_VALUE ? pq.size() : result;
    }

    class Tuple implements Comparable<Tuple> {
        int start;
        int end;

        public Tuple(int start, int end) {
            this.start = start;
            this.end = end;
        }

        public int compareTo(Tuple tuple) {
            return this.start > tuple.start ? 1 : this.start < tuple.start ? -1 : 0;
        }
    }
}
