```
class Solution {
public:
    int leastBricks(vector<vector<int>>& wall) {
        unordered_map<int,int> end_count;
        int end_brick = 0, maks = 0;
        int row = wall.size(), col;
        for(int i=0; i<row; i++){
            end_brick = 0;
            col = wall[i].size() - 1;
            for(int j=0; j<col; j++){
                end_brick += wall[i][j];
                if(end_count.find(end_brick)!=end_count.end()){
                    end_count[end_brick]++;
                }
                else{
                    end_count[end_brick] = 1;
                }
                maks = max(maks,end_count[end_brick]);
            }
        }
        return row - maks;
    }
};
